export const formatValue = (value: number): string =>
  new Intl.NumberFormat([], {
    style: 'currency',
    currency: 'BRL',
  }).format(value);

export const formatDate = (value: Date): string =>
  new Intl.DateTimeFormat('pt-BR').format(value);
