import React, { useState } from 'react';

import { Link } from 'react-router-dom';

import { Container } from './styles';

import Logo from '../../assets/logo.svg';

interface HeaderProps {
  size?: 'small' | 'large';
}

const Header: React.FC<HeaderProps> = ({ size = 'large' }: HeaderProps) => {
  const [activeLinkHome, setActiveLinkHome] = useState(true);
  const [activeLinkList, setActiveLinkList] = useState(false);

  return (
    <Container size={size}>
      <header>
        <img src={Logo} alt="GoFinances" />
        <nav>
          <Link
            to="/"
            className={activeLinkHome ? 'activeLink' : 'test'}
            onClick={() => {
              setActiveLinkHome(true);
              setActiveLinkList(false);
            }}
          >
            Listagem
          </Link>
          <Link
            to="/import"
            className={activeLinkList ? 'activeLink' : 'test'}
            onClick={() => {
              setActiveLinkList(true);
            }}
          >
            Importar
          </Link>
        </nav>
      </header>
    </Container>
  );
};

export default Header;
